mapper.select:
select callback from t_funtime_kuaishou_ad_monitor where android_id3 = #{androidId}

    or imei3 = #{imei}


    or oaid = #{oaid}

order by id desc
limit 1

select callback_url from t_funtime_toutiao_ad_monitor where  androididmd5 = #{androidId}

or imeimd5 = #{imei}


or oaid = #{oaid}

order by id desc
limit 1

