mapper.select:
SELECT count(id) FROM `t_funtime_game_yaoyao_show_conf`
where type = #{type}
 and DATE_FORMAT(NOW(),'%Y%m%d')+0 BETWEEN day_start AND day_end
 and DATE_FORMAT(NOW(),'%H%i')+0 BETWEEN hour_start AND hour_end;

mapper.update:
update  t_funtime_game_yaoyao_pool
set actual_pool = actual_pool+#{amount}
where id = #{id}

update  t_funtime_game_yaoyao_pool
set actual_pool = actual_pool-#{amount}
where id = #{id} and actual_pool-#{amount}>=0
