mapper.select:
select d.user_id as userId,d.service_text as serviceText,unix_timestamp(d.create_time) createTime,d.tag_text tagText, d.price,d.game, u.nickname,u.portrait_address as portraitAddress,u.sex,c.room_id as roomId, case when h.user_id is null then null when h.type = 1 then f.level_url when h.type = 2 then (select w.headwear_url from t_funtime_headwear w where w.headwear_number = h.headwear_id limit 1) else null end levelUrl, (select GROUP_CONCAT(g.tag_name,'/',ifnull(g.tag_color,'')) from t_funtime_tag g where g.tag_type = 'order' and FIND_IN_SET(g.id,d.service_tag)>0 ) as tags from t_funtime_order d inner join t_funtime_user u on d.user_id = u.id inner join (t_funtime_user_account a inner join t_funtime_user_level_conf f on a.level = f.level) on d.user_id = a.user_id left join t_funtime_user_headwear_current h on u.id = h.user_id left join t_funtime_chatroom_mic c on d.user_id = c.mic_user_id and u.online_state = 1 where d.order_state = 1
and d.create_time < from_unixtime(#{lastId})
and FIND_IN_SET(#{tagId},d.service_tag)>0
and u.sex = #{sex}
order by d.create_time desc limit ${counts}

mapper.insert:
insert into t_funtime_order (user_id, order_time, start_hour,
end_hour, service_tag, tag_text,game,
price, order_state,
 service_text
)
values (#{userId,jdbcType=BIGINT}, #{orderTime,jdbcType=VARCHAR}, #{startHour,jdbcType=VARCHAR},
#{endHour,jdbcType=VARCHAR}, #{serviceTag,jdbcType=VARCHAR}, #{tagText,jdbcType=VARCHAR},#{game},
#{price}, #{orderState,jdbcType=INTEGER},
#{serviceText,jdbcType=LONGVARCHAR}
)

mapper.select:
select d.id,d.tag_name as tagName,d.price,d.counts,d.total_red as total,d.state,DATE_FORMAT(d.create_time,'%Y-%m-%d %H:%i') as createTime
,u.sex,u.portrait_address as portraitAddress,u.nickname
from t_funtime_user_order_record d inner join t_funtime_user u on d.user_id = u.id
where d.to_user_id = #{userId}

    and d.state in (1,2,4)


    and d.state in (3,5,6,7)


    and d.id < #{lastId}

order by d.id desc
limit ${counts}

select d.id,d.tag_name as tagName,d.price,d.counts,d.total as total,d.state,DATE_FORMAT(d.create_time,'%Y-%m-%d %H:%i') as createTime
,u.sex,u.portrait_address as portraitAddress,u.nickname
from t_funtime_user_order_record d inner join t_funtime_user u on d.to_user_id = u.id
where d.user_id = #{userId}

    and d.state in (1,2,4)


    and d.state in (3,5,6,7)


    and d.id < #{lastId}

order by d.id desc
limit ${counts}

select id, order_no as orderNo, user_id as userId, to_user_id as toUserId, poundage, total_red as totalRed,total,
    state
from t_funtime_user_order_record where id = #{id}
