mapper.select:
select d.id, d.amount, d.num, d.gift_id, d.gift_name, d.to_user_id, d.create_time,u.nickname,g.animation_type, g.animation_url,g.image_url, (select count(c.id) from t_funtime_user_concern c where c.user_id = #{userId} and c.to_user_id = d.to_user_id) concerned from t_funtime_user_account_gifttrans_record d inner join t_funtime_user u on d.to_user_id = u.id inner join t_funtime_gift g on d.gift_id = g.id where d.user_id = #{userId}
and d.create_time between #{startDate} and #{endDate}
order by d.id desc

select d.id, d.amount, d.num, d.gift_id, d.gift_name, d.to_user_id, d.create_time,u.nickname,g.animation_type, g.animation_url,g.image_url, (select count(c.id) from t_funtime_user_concern c where c.user_id = #{userId} and c.to_user_id = d.user_id) concerned from t_funtime_user_account_gifttrans_record d inner join t_funtime_user u on d.user_id = u.id inner join t_funtime_gift g on d.gift_id = g.id where d.to_user_id = #{userId}
and d.create_time between #{startDate} and #{endDate}
order by d.id desc
